	public function storeAbility()
	{
		$name = $_POST['name'];
		$used_for = $_POST['used_for'];

		$ability = new Ability();
		$ability->id = $this->newID(Ability::class);
		$ability->name = $name;
		$ability->type = $used_for;
		$ability->save();
		return redirect()->back();
	}

	public function storeGate()
	{
		$name = $_POST['name'];
		$ability_id = $_POST['ability_id'];

		$gate = new \App\AbilityGate();
		$gate->id = $this->newID(\App\AbilityGate::class);
		$gate->ability_id = $ability_id;
		$gate->name = $name;
		$gate->save();
		return redirect()->back();
	}
